import React from 'react'
import Settings from './settings'
import mui from 'material-ui'
import FixedDataTable, {Table, Column, ColumnGroup as Group} from 'fixed-data-table'

let DataTable = React.createClass({

  propTypes: {
    colNames: React.PropTypes.array,
    data: React.PropTypes.array
  },

  rowGetter: function(rowIndex) {
      return this.props.data[rowIndex];
  },

  getWidth: function() {
    let width = window.innerWidth - Settings.leftNavWidth - (2 * Settings.contentPadding) - Settings.widthBuffer;
    return width;
  },

  handleResize: function() {
    this.setState({width: this.getWidth()});
  },

  componentDidMount: function() {
    window.addEventListener('resize', this.handleResize);
  },

  getInitialState: function() {
    return { width: this.getWidth() };
  },

  style: function() {
    return {
      width: this.state.width + "px",
      margin: "20px 0"
    };
  },

  render: function() {
    let columns =
      <Group fixed={true}>
        {
          this.props.colNames.map((name, i) =>
            <Column
              label={name}
              dataKey={i}
              width={this.state.width / this.props.colNames.length} />
          , this)
        }
      </Group>;

    return (
      <div style={this.style()} className="table">
        <Table
          rowHeight={50}
          rowGetter={this.rowGetter}
          rowsCount={this.props.data.length}
          width={this.state.width}
          height={400}
          headerHeight={50}>
            {columns}
        </Table>
      </div>
    );
  }
});

export default DataTable;
