
import React from 'react'

import { GizmoQueries } from '../data/gizmo'
import TestAction from '../actions/testAction'

export default class Widget extends React.Component {
  makeGizmo() {
    TestAction.createGizmo(Date.now())
  }
  render() {
    return (
      <div>
        <label>Make a new gizmo!</label>
        <input type="text" />
        <button onClick={this.makeGizmo}>Create</button>
        <hr />
        <h3>Existing gizmos</h3>
        <ul>
          {
            GizmoQueries.all().map(g => <li>{g.name}</li>)
          }
        </ul>
      </div>
    );
  }
}
