import _ from 'lodash'
import Store from '../store'

function nonzero(v) {
  return  v.b  != 0 ||
          v.b1 != 0 ||
          v.b2 != 0 ||
          v.b3 != 0 ||
          v.b4 != 0;
}

function all(v) {
  return true
}

export function getActive(includeZeros) {
  return Store.data
    .get('agingReports')
    .filter(v => v.active == 'Active')
    .filter(includeZeros ? all : nonzero)
    .toMap()
}

export function getInactive(includeZeros) {
  return Store.data
    .get('agingReports')
    .filter(v => v == 'Inactive')
    .filter(includeZeros ? all : nonzero)
    .toMap()
}

export function getAll(includeZeros) {
  return Store.data
    .get('agingReports')
    .filter(includeZeros ? all : nonzero)
    .toMap()
}
