import _ from 'lodash'
import Store from '../store'

function nonzero(v) {
  return  v.get('balance')  != 0 ||
          v.get('b_0_30') != 0 ||
          v.get('b_31_60') != 0 ||
          v.get('b_61_90') != 0 ||
          v.get('b_91') != 0;
}

function all(v) {
  return true
}

export function getActive(includeZeros) {
  return Store.data
    .get('agingReports')
    .filter(v => v.get('active') == 'Active')
    .filter(includeZeros ? all : nonzero)
    .toMap()
}

export function getInactive(includeZeros) {
  return Store.data
    .get('agingReports')
    .filter(v => v.get('active') == 'Inactive')
    .filter(includeZeros ? all : nonzero)
    .toMap()
}

export function getAll(includeZeros) {
  return Store.data
    .get('agingReports')
    .filter(includeZeros ? all : nonzero)
    .toMap()
}
